import React, { useEffect, useState } from "react";
import { getDataMenu } from "./getDataMenu";
import styles from './LayOutDataMenu.module.scss'

const LayOutDataMenu = ({ type }) => {
    const [data, setData] = useState([])
    const [page, setPage] = useState(1)
    useEffect(
        () => {
            const fetchApi = async () => {
                console.log(page)
                const response = await getDataMenu(type, page);
                setData(response)
            }
            fetchApi();
        }, [type, page]
    )
    useEffect(
        () => {
            setPage(1)
        }, [type]
    )
    function handleNextPage() {
        setPage(prev => prev + 1)
        window.scrollTo({
            top: 0,
            behavior: `smooth`
        })
    }
    function handlePreviousPage() {
        setPage(prev => prev - 1)
        window.scrollTo({
            top: 0,
            behavior: `smooth`
        })
    }

    function handleClick(e) {
        console.log(e)
    }
    return (
        <div className={styles.menu}>
            {
                data.map(
                    (item, index) => {
                        return <div key={index} className={styles.item} onClick={(e)=>handleClick(e)}>
                            <img src={item.img} />
                            <p>{item.type}</p>
                            <h4>{item.name}</h4>
                            <h5>{item.min_price}</h5>
                        </div>
                    }
                )
            }
            <div className={styles.footer}>
                <button className={styles.changePage} onClick={handlePreviousPage}>
                    <i className={`fa-solid fa-chevron-left ${styles.icon}`}></i>
                    Previous Page
                </button>
                {page}
                <button className={styles.changePage} onClick={handleNextPage}>
                    Next Page
                    <i className={`fa-solid fa-chevron-right ${styles.icon}`}></i>
                </button>
            </div>
        </div>
    )
}

export default LayOutDataMenu
