import { VND } from "../changeCurrency/changeCurrency";
import { getRateJp, getRateKor } from "../rate"
import { instance, instanceKream } from "../services"
import { getIdProductKream } from "../getDataFromKream/getIdProductKream";

export const getDataMenu = async (type, page) => {
    const response = await instance.get(`https://www.soldout.co.kr/api/v3/search/get-item-list?&typeGb=goods&pageIndex=${page}&pageSize=12&keyword=${type}`)
    const rateKor = await getRateKor();
    const rateJp = await getRateJp();
    const data = response.data.data.product_info.list.map(
        (item) => {
            for (let i = 0; i < item.model_no.length; i++) {
                if (item.model_no[i] == ' ' || item.model_no[i] == '/') {
                    item.model_no = item.model_no.slice(0, -item.model_no.length + i)
                }
            }
            // const responseJP = await instance.get(`https://snkrdunk.com/v1/products/CW2288-111/sales-history?`)
            // console.log(responseJP)
            return {
                code: item.model_no,
                type: item.brand_name_eng,
                min_priceSoldOut: VND.format(Math.floor(item.min_price*rateKor)),
                name: item.name_eng,
                img: 'https://kream-phinf.pstatic.net/MjAyMzA3MjZfOTkg/MDAxNjkwMzM1OTAwMjkz.RXuQ8M7PrHUX1k1HvKUlyq9bifTEJLSBJ-EncInPub4g.r3fXPJvYOFyh98ARfA181aUpJyfnEW06j3ajU0kgo24g.PNG/a_efed3f6145c34b88877d98b09cb5dc64.png'
            }

        }
    )
    const arrKor = []
    for(let i = 0; i<data.length;i++) {
        const result = await data[i]
        arrKor.push(result)
    }
    // for(let i=0; i<data.length; i++) {
    //     arrKor[i] = {
    //         ...arrKor[i],
    //         a:1
    //     }
    // }

    for(let i=0; i<arrKor.length; i++) {
        try {
            const responseJP = await instance.get(`https://snkrdunk.com/v1/products/${arrKor[i].code}/sales-history?`)
            const kreamId = await getIdProductKream(arrKor[i].code)
            const responseKream = await instanceKream.get(`https://kream.co.kr/api/p/products/${kreamId}?request_key=cadf87d7-7b0e-4f85-8bf3-9771608a29f5`)
            console.log(responseKream)
            console.log(arrKor[i], responseJP)
            arrKor[i] = {
                ...arrKor[i],
                min_priceJp: VND.format(Math.floor(responseJP.data.minPrice*rateJp)),
                min_priceKream: VND.format(Math.floor(responseKream.data.market.lowest_ask*rateKor))
            }
        } catch(msg) {
            return arrKor[i]
        }
    }

    console.log(arrKor)




    return arrKor
}
