import React, { useState, useEffect } from "react";
import styles from './LayOut.module.scss'
import GetDataFromSNKRDUNK from "../getDataFromSNKRDUNK";
import GetDataFromSoldOut from "../getDataFromSoldOut/getDataFromSoldOut";
import { getData } from "../getDataFromSNKRDUNK/getDataJp";
import { mergeData } from "../mergeData";

function Layout({ submitCode }) {
    const [data, setData] = useState([]);
    // useEffect(
    //     () => {
    //         const fetchApi = async() => {
    //             const dataJP = await getData(submitCode);
    //             setDataJP(dataJP)
    //         }
    //         fetchApi()

    //     }, [submitCode]
    // )
    useEffect(
        () => {
            if (!submitCode) {
                console.log(data.price)
                return
            }
            const fetchData = async () => {
                const data = await mergeData(submitCode);
                console.log(data)
                setData(data)
            }
            fetchData()
        }, [submitCode]
    )
    return (
        <div className={styles.wrapper}>
            <div className={styles.image}>
                {!data.code || <img src={data.img} />}
            </div>
            <div className={styles.infor}>
                <h3>{data.name}</h3>
                <p>{data.code}</p>
                <div className={styles.details}>
                    {
                        data.price && data.price.length > 0 && <table>
                            <tbody>
                                <tr className={styles.priceAndSize}>
                                    <th>Size</th>
                                    <th>Kor</th>
                                    <th>Jap</th>
                                </tr>

                            </tbody>
                            {data.price.map(
                                (item) => {
                                    if (!item.size) {
                                        return <></>
                                    }
                                    return <tbody>
                                        <tr key={item.size} className={styles.priceAndSize}>
                                            <td>{item.size}</td>
                                            <td>{item.priceKor}</td>
                                            <td>{item.priceJp}</td>
                                        </tr>
                                    </tbody>
                                }
                            )}
                        </table>
                    }
                </div>

            </div>
        </div>
    )
}

export default Layout
