          <button className={styles.itemHeader} onClick={(e)=>handleChoose(e)}>Air Force 1</button>
          <button className={styles.itemHeader} onClick={(e)=>handleChoose(e)}>Air Max</button>
        </div>
        <div className={styles.search}>
          <input
            placeholder='Code...'
            className={styles.input}
            value={code}
            onChange={(e) => setCode(e.target.value)}
          />
          <input
            placeholder='Size...'
            className={styles.input}
            style={{ width: '50px' }}
            value={size}
            onChange={(e) => setSize(e.target.value)}
          />
          <button className={styles.btn} onClick={handleClick}>Submit</button>
        </div>
        <div className={styles.filter}>
        </div>

      </div> */}
      <div className={styles.wrapper}>
        <Layout submitCode={submitCode} size={submitSize} />
      </div>
      {
        modal && <div className={styles.modal}>
          <LayOutDataMenu type={type} handleClick={(e) => {
            setSubmitCode(e.code)
            setModal(false)
          }} />
        </div>

      }
    </div>
  );
}

export default App;
