import axios from 'axios'
import { useEffect, useState } from 'react'
import styles from './App.module.scss';
import Layout from './LayOut';
import { Link } from 'react-router-dom'
import LayOutDataMenu from './DataMenu/LayOutDataMenu';

function App() {
  const [code, setCode] = useState('')
  const [submitCode, setSubmitCode] = useState('');
  const [size, setSize] = useState('');
  const [submitSize, setSubmitSize] = useState('');
  const [modal, setModal] = useState(false);
  const [type, setType] = useState();


  const handleClick = () => {
    setSubmitCode(code)
    setSubmitSize(size)
    setModal(false)
    setCode('')
    setSize('')
    window.scrollTo({
      top: 0,
      behavior: `smooth`
    })
  }

  function handleChoose(e) {
    const value = e.target.innerHTML
    setModal(true)
    setSubmitCode('')
    setType(value)
  }

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <div className={styles.type}>
          <button className={styles.itemHeader} onClick={(e)=>handleChoose(e)}>Jordan</button>
          <button className={styles.itemHeader} onClick={(e)=>handleChoose(e)}>Air Force 1</button>
          <button className={styles.itemHeader} onClick={(e)=>handleChoose(e)}>Air Max</button>
        </div>
        <div className={styles.search}>
          <input
            placeholder='Code...'
            className={styles.input}
            value={code}
            onChange={(e) => setCode(e.target.value)}
          />
          <input
            placeholder='Size...'
            className={styles.input}
            style={{ width: '50px' }}
            value={size}
            onChange={(e) => setSize(e.target.value)}
          />
          <button className={styles.btn} onClick={handleClick}>Submit</button>
        </div>
        <div className={styles.filter}>
        <Link to="/about">About</Link>
        </div>

      </div>
      <div className={styles.wrapper}>
        <Layout submitCode={submitCode} size={submitSize} />
      </div>
      {
        modal && <div className={styles.modal}>
          <LayOutDataMenu type={type} handleClick={(e) => {
            setSubmitCode(e.code)
            setModal(false)
          }} />
        </div>

      }
    </div>
  );
}

export default App;
