import React, { useState, useEffect } from "react";
import styles from './LayOut.module.scss'
import GetDataFromSNKRDUNK from "../getDataFromSNKRDUNK";
import GetDataFromSoldOut from "../getDataFromSoldOut/getDataFromSoldOut";
import { getData } from "../getDataFromSNKRDUNK/getDataJp";
import { mergeData } from "../mergeData";

function Layout({ submitCode }) {
    const [data, setData] = useState([]);
    // useEffect(
    //     () => {
    //         const fetchApi = async() => {
    //             const dataJP = await getData(submitCode);
    //             setDataJP(dataJP)
    //         }
    //         fetchApi()

    //     }, [submitCode]
    // )
    useEffect(
        () => {
            if (!submitCode) {
                console.log(data.price)
                return
            }
            const fetchData = async () => {
                const data = await mergeData(submitCode);
                console.log(data)
                setData(data)
            }
            fetchData()
        }, [submitCode]
    )
    return (
        <div className={styles.wrapper}>
            <div className={styles.image}>
                {!data.code || <img src={data.img} />}
            </div>
            <div className={styles.infor}>
                <h3>{data.name}</h3>
                <p>{data.code}</p>



                    {
                        data.price && data.price.length > 0 && data.price.map(
                            (item) => {
                                console.log(item)
                                return <li className={styles.priceAndSize}>
                                    <p>{item.size}</p>
                                    <p>{item.priceKor}</p>
                                    <p>{item.priceJp}</p>
                                </li>
                            }
                        )
                    }

            </div>
        </div>
    )
}

export default Layout
