                        <p>{item.priceJp}</p>
                    </li>
                    {
                        data.price && data.price.length > 0 && data.price.map(
                            (item) => {
                                console.log(item)
                                return <li className={styles.priceAndSize}>
                                    <p>{item.size}</p>
                                    <p>{item.priceKor}</p>
                                    <p>{item.priceJp}</p>
                                </li>
                            }
                        )
                    }
                </ul>
            </div>
        </div>
    )
}

export default Layout
