import { getRateKor } from "../rate"
import { instanceKream, instanceKreamWithToken } from "../services"
import { getIdProductKream } from "./getIdProductKream"


export const getSalesHistory = async(submitCode) => {
    const id = await getIdProductKream(submitCode)
    const response = await instanceKreamWithToken.get(`https://kream.co.kr/api/p/products/${id}/sales?cursor=1&per_page=50&sort=date_created[desc]&request_key=428133c9-cffe-44fd-8f39-e1b2f95b9a20`)
    const rate = await getRateKor();
    const data = response.data.items
    console.log(data)
    const arr = [];
    for (let i=0; i<20; i++) {
        arr.push(
            {
                size: parseInt(data[i].option),
                price: Math.floor(data[i].price*rate),
                date: data[i].date_created.split()
            }
        )
    }
    return arr;
}
