import { getRateKor } from "../rate";
import { instanceKream } from "../services";

export const getSizeAndSkuFromKream = async (submitCode) => {
    const data = await instanceKream.get('https://kream.co.kr/api/p/products/12831?request_key=4db5528c-a6f9-4256-a16e-79d3d4dd9d8b')
    const response = data.data.sales_options
    const rate = await getRateKor();
    console.log(rate)
    const result = response.map(
        (item) => {
            return {
                size: parseInt(item.option)*rate,
                price: item.lowest_ask
            }
        }
    )
    return result
}
